import {useState} from 'react'
import Switch from './SwitchDemo';
import styles from './NewTransactionForm.module.css';
import Input from './form/Input'
import Select from './form/Select'

function NewTransactionForm() {
    const [isIncome, setIsIncome] = useState(true) 
    const [title, setTitle] = useState('')
    const [amount, setAmount] = useState('')
    const [date, setDate] = useState('')
    const [categories, setCategories] = useState([])
    const [titlePlaceholder, setTitlePlaceholder] = useState('Salário, Serviço Prestado, etc...')

    fetch("http://localhost:5000/categories", {
        metohd: "GET",
        headers: {'Content-Type': 'application/json'}
    })
        .then((response) => response.json())
        .then((data) => {
            setCategories(data)
        })
        .catch((err) => console.log(err))

    function handleCreateNewTransaction(e) {
        e.preventDefault()
        console.log(isIncome)
        console.log(title)
        console.log(amount)
        console.log(date)
    }

    return (
        <>
            <h2>Nova transação</h2>
            <form className={styles.form} onSubmit={handleCreateNewTransaction}>
                <label className={styles.label}>Tipo da transação</label>
                <div className={styles.switchDiv}>
                    <Switch 
                        setIsIncome={setIsIncome} 
                        setTitlePlaceholder={setTitlePlaceholder} 
                        isIncome={isIncome}
                    />
                    <small className={styles.smallInfo}>Receita para valor que entrou na sua conta. Despesa para valor que saiu da sua conta. </small>
                </div>
                
                <Select 
                    name="categorie"
                    text="Selecione uma categoria"
                    options={categories}
                />

                <Input 
                    name="title"
                    text="Nome"
                    type="text"
                    placeholder={titlePlaceholder}
                    handleOnChange={(e) => setTitle(e.target.value)}
                    value={title}
                />
                <Input
                    name="amount"
                    text="Valor"
                    type="number"
                    placeholder="0.00"
                    handleOnChange={(e) => setAmount(e.target.value)}
                    value={amount}
                />

                <Input
                    name="date"
                    text="Data da transação"
                    type="date"
                    handleOnChange={(e) => setDate(e.target.value)}
                    value={date}
                />
                <input className={styles.inputSubmit} type='submit'></input>
            </form>
        </>
    )
}

export default NewTransactionForm;
