import styles from './CurrentBalance.module.css'
import {useState} from 'react'

function CurrentBalance({transactions}) {
    const [balance, setBalance] = useState(0)

    const updateBalanceValue = () => {
        const transactionsAmounts = transactions.map(function(transaction) {
            return parseFloat(transaction.amount)
        })

        const total = transactionsAmounts  
            .reduce(function(accumulator, transaction){
                return accumulator + transaction
            }, 0)  
            .toFixed(2)

        setBalance(total)
    }

    return (
        <div style={{textAlign:'center'}}>
            <h2 className={styles.tituloSaldoAtual}>SALDO ATUAL</h2>
            <h1 className={styles.saldoAtual}>R$ {balance}</h1>
        </div>
    )
}

export default CurrentBalance
