import {useState, useEffect} from 'react'
import Switch from './SwitchDemo';
import styles from './NewTransactionForm.module.css';
import Input from './form/Input'
import Select from './form/Select'
import { v4 as uuidv4 } from 'uuid';

const currentDate = new Date().toISOString().split('T')[0];

function NewTransactionForm() {
    
    const [isIncome, setIsIncome] = useState(true) 
    const [title, setTitle] = useState('')
    const [amount, setAmount] = useState('')
    const [date, setDate] = useState(currentDate)
    const [categories, setCategories] = useState([])
    const [categorieId, setCategorieId] = useState('')
    const [titlePlaceholder, setTitlePlaceholder] = useState('Salário, Serviço Prestado, etc...')

    const fetchCategories = () => {
        const url = isIncome ? "http://localhost:5000/income_categories" : "http://localhost:5000/expense_categories" 
        fetch(url, {
            metohd: "GET",
            headers: {'Content-Type': 'application/json'}
        })
        .then((response) => response.json())
        .then((data) => {
            setCategories(data)
        })
        .catch((err) => console.log(err))
    }

    function handleCreateNewTransaction(e) {
        e.preventDefault()
        if(categorieId === "") {
            alert("Selecione uma categoria!")
            return false
        } else {
            if(title.trim().length<3) {
                alert("Preencha o campo 'Nome' com pelo menos 3 caracteres")
                return false
            } else {
                if(parseFloat(amount) < 0) {
                    alert("Digite um 'Valor' para a transação")
                    return false
                }
            }
        }

        const newTransaction = {
            "id": uuidv4(),
            "categorie_id": categorieId,
            "title": title,
            "amount": isIncome ? amount : -amount,
            "date": date
        }

        fetch("http://localhost:5000/transactions", {
            method: "POST",
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(newTransaction)
        })
        .then(response => {
            if (!response.ok) {
                throw new Error(`Erro na requisição: ${response.status}`);
            }
            return response.json(); 
        })
        .then(data => console.log(data))
        .catch((err) => {console.log(err)})

        setCategorieId("")
        setTitle("")
        setAmount("")
    }

    useEffect(() => {
        fetchCategories();
    }, [isIncome, fetchCategories]);

    return (
        <>
            <h2>Nova transação</h2>
            <form className={styles.form} onSubmit={handleCreateNewTransaction}>
                <label className={styles.label}>Tipo da transação</label>
                <div className={styles.switchDiv}>
                    <Switch 
                        setIsIncome={setIsIncome} 
                        setTitlePlaceholder={setTitlePlaceholder} 
                        isIncome={isIncome}
                    />
                    <small className={styles.smallInfo}>Receita para valor que entrou na sua conta. Despesa para valor que saiu da sua conta. </small>
                </div>
                
                <Select 
                    name="categorie"
                    text="Selecione uma categoria"
                    options={categories}
                    handleOnChange={(e) => setCategorieId(e.target.value)}
                />

                <Input 
                    name="title"
                    text="Nome"
                    type="text"
                    placeholder={titlePlaceholder}
                    handleOnChange={(e) => setTitle(e.target.value)}
                    value={title}
                />
                <Input
                    name="amount"
                    text="Valor"
                    type="number"
                    placeholder="0.00"
                    handleOnChange={(e) => setAmount(e.target.value)}
                    value={amount}
                />

                <Input
                    name="date"
                    text="Data da transação"
                    type="date"
                    handleOnChange={(e) => setDate(e.target.value)}
                    value={date}
                />
                <input className={styles.inputSubmit} type='submit'></input>
            </form>
        </>
    )
}

export default NewTransactionForm;
